// â”€â”€â”€ 0. Tiny helpers (pure-JS) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function addMonths(date, delta) {
	const d = new Date(date);
	d.setMonth(d.getMonth() + delta);
	return d;
}
function addYears(date, delta) {
	const d = new Date(date);
	d.setFullYear(d.getFullYear() + delta);
	return d;
}
const iso = (d) => d.toISOString().slice(0, 10); // "YYYY-MM-DD"
const today = new Date(); // define once for consistency
// â”€â”€â”€ 1. Grab inputs â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const { instrument_key = '', term = '',stock ='' } = $input.first().json.body ?? {};

// â”€â”€â”€ 2. Term â†’ preferred chart intervals â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const termToIntervals = {
	intraday: ['3m', '15m'],
	short:    ['15m', '1h'],
	medium:   ['1h',  '4h']
};
const intervals = termToIntervals[term.toLowerCase()] ?? [];

// â”€â”€â”€ 3. Parse "4h" â†’ { unit:'hours', interval:4 } â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function parse(frame) {
	const n = Number(frame.match(/^\d+/)?.[0] ?? 1);
	const s = frame.replace(/^\d+/, '').toLowerCase();
	if (s === 'm') return { unit:'minutes', interval:n };
	if (s === 'h') return { unit:'hours',   interval:n };
	if (s === 'd') return { unit:'days',    interval:n };
	if (s === 'w') return { unit:'weeks',   interval:n };
	throw new Error(`Unknown frame "${frame}"`);
}

// â”€â”€â”€ 4. Upstox retention windows (your matrix) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function windowFor({ unit, interval }) {
	const today = new Date();
	if (unit === 'minutes') {
		return interval <= 15
			? { from:addMonths(today, -1), to:today }     // 1 month
			: { from:addMonths(today, -3), to:today };    // 1 quarter
	}
	if (unit === 'hours')   return { from:addMonths(today, -3), to:today }; // 1 quarter
	if (unit === 'days')    return { from:addYears(today,  -10), to:today }; // 1 decade
	// weeks / months â†’

 â†’ start 2000-01-01
	return { from:new Date('2000-01-01'), to:today };
}

// â”€â”€â”€ 5. Build every historical-candle URL â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const endpoints = intervals.map((f) => {
	const { unit, interval }  = parse(f);
	const { from, to }        = windowFor({ unit, interval });
	return `https://api.upstox.com/v3/historical-candle/${instrument_key}/${unit}/${interval}/${iso(to)}/${iso(from)}`;
});

const yesterday = new Date(today);
yesterday.setDate(today.getDate() - 1);

// â”€â”€â”€ 6. Output a single item with all URLs â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
return [{
	json: { instrument_key, term, endpoints, stock, today: iso(today),prevDayFromCurrent:iso(yesterday) },
}];

// Initialize an array to collect all titles
let titles = [];

// Loop through each input item
for (const item of $input.all()) {
  // Add title to the array
  titles.push(item.json.title);
}

// Return the array of titles as a single item
return [{
  json: {
    titles: titles,
    term:$('Webhook').first().json.query.term
  }
}];

const inputItems = $input.first().json.data

// Extract URLs and generate dynamic interval keys
const firstUrl = $('Code').first().json.endpoints[0];
const secondUrl = $('Code').first().json.endpoints[1];

function extractOutputKey(url) {
  const match = url.match(/\/(minutes|hours|days|weeks|months)\/(\d+)/);
  if (!match) return null;

  const unitMap = {
    minutes: "m",
    hours: "h",
    days: "d",
    weeks: "w",
    months: "mo"
  };

  const unit = match[1];
  const value = match[2];
  const suffix = unitMap[unit] ? `${value}${unitMap[unit]}` : null;
  return suffix ? `candles${suffix}` : null;
}

const output = [];

// Loop through each item and attach candles under correct dynamic key
for (let i = 0; i < inputItems.length; i++) {
  const item = inputItems[i];
  const dataArray = item.data.candles;

  const url = i === 0 ? firstUrl : secondUrl;
  const outputKey = extractOutputKey(url);

  if (!outputKey || !Array.isArray(dataArray)) continue;

  let candleArray = [];

  for (const entry of dataArray) {
  if (!Array.isArray(entry) || entry.length < 6) continue;

  candleArray.push({
    time: entry[0],
    open: entry[1],
    high: entry[2],
    low: entry[3],
    close: entry[4],
    volume: entry[5]
  });
}

  output.push({
    json: {
      [outputKey]: candleArray
    }
  });
}

return output;
